import React, { useState, useRef } from "react";
import { ArrowLeft, Image, Video } from "lucide-react";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Textarea } from "../components/ui/textarea";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../components/AuthGuard";
import { addMockPost } from "../data/mockData";

const CreatePost = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  const { user } = useAuth();
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [caption, setCaption] = useState("");
  const [location, setLocation] = useState("");
  const [postType, setPostType] = useState("photo");

  const handleFileSelect = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setSelectedFile(file);
      setPreviewUrl(url);
    }
  };

  const handleShare = () => {
    if (!selectedFile) {
      alert("Please select a file first!");
      return;
    }

    const newPost = {
      id: Date.now(),
      username: user?.username || "new_user",
      userAvatar: user?.avatar || "/images/anu1.jpg",
      imageUrl: previewUrl,
      location,
      likes: 0,
      caption,
      timeAgo: "Just now",
      comments: []
    };

    addMockPost(newPost);
    alert("âœ… Post created successfully!");
    navigate("/home");
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      {/* Header */}
      <div className="sticky top-0 bg-white dark:bg-gray-800 border-b border-gray-300 dark:border-gray-700 z-10">
        <div className="flex items-center justify-between p-4">
          <Button variant="ghost" onClick={() => navigate(-1)}>
            <ArrowLeft className="h-6 w-6" />
          </Button>
          <h1 className="font-semibold dark:text-white">Create new post</h1>
          <Button
            variant="ghost"
            className="text-blue-500 font-semibold"
            onClick={handleShare}
          >
            Share
          </Button>
        </div>
      </div>

      <div className="p-6 max-w-lg mx-auto">
        {!previewUrl ? (
          <div className="flex flex-col items-center justify-center">
            <div className="w-24 h-24 rounded-full bg-gray-100 dark:bg-gray-700 flex items-center justify-center mb-4">
              {postType === "photo" ? (
                <Image className="h-12 w-12 text-gray-400" />
              ) : (
                <Video className="h-12 w-12 text-gray-400" />
              )}
            </div>
            <p className="text-gray-500 dark:text-gray-300 mb-4">
              Select a {postType}
            </p>
            <Button
              className="bg-blue-500 hover:bg-blue-600"
              onClick={() => fileInputRef.current?.click()}
            >
              Choose File
            </Button>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*,video/*"
              onChange={handleFileSelect}
              className="hidden"
            />
          </div>
        ) : (
          <>
            <div className="mb-4">
              {postType === "photo" ? (
                <img
                  src={previewUrl}
                  alt="Preview"
                  className="w-full rounded-lg"
                />
              ) : (
                <video
                  src={previewUrl}
                  controls
                  className="w-full rounded-lg"
                />
              )}
            </div>

            <Textarea
              placeholder="Write a caption..."
              value={caption}
              onChange={(e) => setCaption(e.target.value)}
              className="mb-4"
            />
            <Input
              type="text"
              placeholder="Add location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="mb-4"
            />
            <Button
              onClick={handleShare}
              className="bg-blue-500 hover:bg-blue-600 w-full"
            >
              Share Post
            </Button>
          </>
        )}
      </div>
    </div>
  );
};

export default CreatePost;
