import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Settings, Grid, Bookmark, UserCheck, MoreHorizontal, LogOut } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../components/ui/tabs';
import { useAuth } from '../components/AuthGuard';
import PostCard from '../components/PostCard';
import FollowersModal from '../components/FollowersModal';
import axios from 'axios';

const Profile = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const { user, logout, refreshUser } = useAuth();
  const [activeTab, setActiveTab] = useState('posts');
  const [userPosts, setUserPosts] = useState([]);
  const [profileUser, setProfileUser] = useState(null);
  const [savedPosts, setSavedPosts] = useState([]);
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollowLoading, setIsFollowLoading] = useState(false);
  const [showFollowersModal, setShowFollowersModal] = useState(false);
  const [showFollowingModal, setShowFollowingModal] = useState(false);
  const isOwnProfile = username === user?.username;

  // Use user context as initial fallback for own profile
  const displayUser = profileUser || (isOwnProfile ? user : null);

  // Fetch latest user info from backend
  useEffect(() => {
    const fetchUser = async () => {
      try {
        // If viewing own profile, use logged-in user's id
        const idOrUsername = isOwnProfile ? user?.username : username;
        if (idOrUsername) {
          const res = await axios.get(`/api/users/${idOrUsername}`);
          setProfileUser(res.data);
        }
      } catch (err) {
        // If API call fails and it's own profile, use user context as fallback
        if (isOwnProfile && user) {
          setProfileUser(user);
        } else {
          setProfileUser(null);
        }
      }
    };
    fetchUser();
  }, [username, user, isOwnProfile]);

  // Fetch posts for the profile user
  useEffect(() => {
    if (displayUser?._id) {
      axios.get(`/api/posts/user/${displayUser._id}`)
        .then(res => setUserPosts(res.data))
        .catch(() => setUserPosts([]));
    }
  }, [displayUser]);

  // Check if current user is following this profile
  useEffect(() => {
    if (displayUser && user && !isOwnProfile) {
      const following = user.following || [];
      setIsFollowing(following.some(followId => 
        followId.toString() === displayUser._id || followId._id?.toString() === displayUser._id
      ));
    }
  }, [displayUser, user, isOwnProfile]);

  // Handle follow/unfollow
  const handleFollow = async () => {
    if (!user || !displayUser || isFollowLoading) return;

    setIsFollowLoading(true);
    try {
      if (isFollowing) {
        await axios.post(`/api/users/${displayUser._id}/unfollow`, {
          followerId: user._id
        });
        setIsFollowing(false);
      } else {
        await axios.post(`/api/users/${displayUser._id}/follow`, {
          followerId: user._id
        });
        setIsFollowing(true);
      }

      // Refresh current user's data to update following list
      await refreshUser();

      // Refresh profile user data to get updated follow counts
      const res = await axios.get(`/api/users/${displayUser.username}`);
      setProfileUser(res.data);

      // Refresh notification count
      window.dispatchEvent(new CustomEvent('refreshNotifications'));
    } catch (err) {
      console.error('Error following/unfollowing user:', err);
    } finally {
      setIsFollowLoading(false);
    }
  };

  // Fetch saved posts for own profile
  useEffect(() => {
    if (isOwnProfile && user?._id) {
      axios.get(`/api/posts/saved/${user._id}`)
        .then(res => setSavedPosts(res.data))
        .catch(() => setSavedPosts([]));
    }
  }, [isOwnProfile, user?._id]);

  const handleEditPost = (post) => {
    // Navigate to edit post page or open edit modal
    console.log('Edit post:', post);
    // TODO: Implement edit functionality
  };

  const handleDeletePost = async (postId) => {
    try {
      await axios.delete(`/api/posts/${postId}`);
      setUserPosts(userPosts.filter(post => post._id !== postId));
    } catch (err) {
      console.error('Error deleting post:', err);
      alert('Failed to delete post');
    }
  };

  const handleSavePost = async (postId, isSaving) => {
    try {
      if (!user?._id) {
        alert('User not found. Please log in again.');
        return;
      }

      if (isSaving) {
        await axios.post(`/api/posts/${postId}/save`, { userId: user._id });
        // Add to saved posts list
        const post = userPosts.find(p => p._id === postId);
        if (post) {
          setSavedPosts([...savedPosts, post]);
        }
      } else {
        await axios.delete(`/api/posts/${postId}/save`, { data: { userId: user._id } });
        // Remove from saved posts list
        setSavedPosts(savedPosts.filter(p => p._id !== postId));
      }
    } catch (err) {
      console.error('Error saving post:', err);
      alert('Failed to save post');
    }
  };

  const handleAddComment = async (postId, commentText) => {
    try {
      if (!user?._id) {
        alert('User not found. Please log in again.');
        return;
      }

      const response = await axios.post(`/api/posts/${postId}/comment`, {
        userId: user._id,
        text: commentText
      });

      // Update the post in the userPosts array with the new comment
      setUserPosts(userPosts.map(post => 
        post._id === postId 
          ? { ...post, comments: [...(post.comments || []), response.data.comment] }
          : post
      ));
    } catch (err) {
      console.error('Error adding comment:', err);
      alert('Failed to add comment');
    }
  };

  const handleLike = async (postId, isLiking) => {
    try {
      if (!user?._id) {
        alert('User not found. Please log in again.');
        return;
      }

      if (isLiking) {
        await axios.post(`/api/posts/${postId}/like`, { userId: user._id });
      } else {
        await axios.delete(`/api/posts/${postId}/like`, { data: { userId: user._id } });
      }

      // Refresh user posts to get updated like count
      if (displayUser?._id) {
        const res = await axios.get(`/api/posts/user/${displayUser._id}`);
        setUserPosts(res.data);
      }
    } catch (err) {
      console.error('Error toggling like:', err);
      alert('Failed to update like');
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* Profile header */}
      <div className="bg-white dark:bg-gray-800 border-b border-gray-300 dark:border-gray-700 lg:border-none">
        <div className="p-4 lg:p-8">
          <div className="flex items-start space-x-4 lg:space-x-8">
            {/* Avatar */}
            <div className="w-20 h-20 lg:w-32 lg:h-32 rounded-full overflow-hidden flex-shrink-0">
              <img
                src={displayUser?.avatar ? `http://localhost:5001/${displayUser.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=150&h=150&fit=crop&crop=face'}
                alt={displayUser?.username}
                className="w-full h-full object-cover"
              />
            </div>

            {/* Profile info */}
            <div className="flex-1 min-w-0">
              {/* Username and buttons */}
              <div className="flex items-center space-x-4 mb-4">
                <h1 className="text-xl lg:text-2xl font-light dark:text-white">{displayUser?.username}</h1>
                {isOwnProfile ? (
                  <div className="flex space-x-2">
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => navigate('/profile/edit')}
                    >
                      Edit profile
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => navigate('/settings')}
                    >
                      <Settings className="h-4 w-4" />
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => {
                        logout();
                        navigate('/login');
                      }}
                      className="text-red-600 hover:text-red-700"
                    >
                      <LogOut className="h-4 w-4" />
                    </Button>
                  </div>
                ) : (
                  <div className="flex space-x-2">
                    <Button 
                      variant={isFollowing ? "outline" : "default"}
                      size="sm"
                      onClick={handleFollow}
                      disabled={isFollowLoading}
                      className={isFollowing ? "" : "bg-blue-500 hover:bg-blue-600 text-white"}
                    >
                      {isFollowLoading ? '...' : isFollowing ? 'Following' : 'Follow'}
                    </Button>
                    <Button variant="outline" size="sm">
                      Message
                    </Button>
                    <Button variant="outline" size="sm">
                      <UserCheck className="h-4 w-4" />
                    </Button>
                    <Button variant="outline" size="sm">
                      <MoreHorizontal className="h-4 w-4" />
                    </Button>
                  </div>
                )}
              </div>

              {/* Stats */}
              <div className="flex space-x-8 mb-4">
                <div className="flex flex-col items-center lg:items-start">
                  <span className="font-semibold dark:text-white">{userPosts.length}</span>
                  <span className="text-sm text-gray-500 dark:text-gray-400">posts</span>
                </div>
                <div 
                  className="flex flex-col items-center lg:items-start cursor-pointer hover:opacity-70 transition-opacity"
                  onClick={() => setShowFollowersModal(true)}
                >
                  <span className="font-semibold dark:text-white">{displayUser?.followers?.length || 0}</span>
                  <span className="text-sm text-gray-500 dark:text-gray-400">followers</span>
                </div>
                <div 
                  className="flex flex-col items-center lg:items-start cursor-pointer hover:opacity-70 transition-opacity"
                  onClick={() => setShowFollowingModal(true)}
                >
                  <span className="font-semibold dark:text-white">{displayUser?.following?.length || 0}</span>
                  <span className="text-sm text-gray-500 dark:text-gray-400">following</span>
                </div>
              </div>

              {/* Bio */}
              <div className="hidden lg:block">
                <h2 className="font-semibold mb-1 dark:text-white">{displayUser?.displayName}</h2>
                <p className="text-sm whitespace-pre-line dark:text-gray-300">{displayUser?.bio}</p>
                {displayUser?.website && (
                  <a
                    href={displayUser?.website}
                    className="text-sm text-blue-600 hover:underline"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {displayUser?.website}
                  </a>
                )}
              </div>
            </div>
          </div>

          {/* Bio for mobile */}
          <div className="lg:hidden mt-4">
            <h2 className="font-semibold mb-1 dark:text-white">{displayUser?.displayName}</h2>
            <p className="text-sm whitespace-pre-line dark:text-gray-300">{displayUser?.bio}</p>
            {displayUser?.website && (
              <a
                href={displayUser?.website}
                className="text-sm text-blue-600 hover:underline"
                target="_blank"
                rel="noopener noreferrer"
              >
                {displayUser?.website}
              </a>
            )}
          </div>
        </div>
      </div>

      {/* Tabs */}
      <Tabs defaultValue="posts" className="w-full">
        <TabsList className="w-full bg-white dark:bg-gray-800 border-b border-gray-300 dark:border-gray-700 rounded-none h-auto p-0">
          <TabsTrigger 
            value="posts" 
            className="flex-1 flex items-center justify-center space-x-2 py-3 border-t-2 border-transparent data-[state=active]:border-black dark:data-[state=active]:border-white rounded-none dark:text-white"
          >
            <Grid className="h-4 w-4" />
            <span className="hidden lg:inline">POSTS</span>
          </TabsTrigger>
          <TabsTrigger 
            value="saved" 
            className="flex-1 flex items-center justify-center space-x-2 py-3 border-t-2 border-transparent data-[state=active]:border-black dark:data-[state=active]:border-white rounded-none dark:text-white"
          >
            <Bookmark className="h-4 w-4" />
            <span className="hidden lg:inline">SAVED</span>
          </TabsTrigger>
        </TabsList>

        <TabsContent value="posts" className="mt-0">
          {userPosts.length > 0 ? (
            <div className="space-y-0 lg:space-y-6">
              {userPosts.map((post) => (
                <PostCard 
                  key={post._id} 
                  post={post} 
                  onEdit={handleEditPost}
                  onDelete={handleDeletePost}
                  onSave={handleSavePost}
                  onAddComment={handleAddComment}
                  onLike={handleLike}
                />
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-16">
              <Grid className="h-16 w-16 text-gray-300 dark:text-gray-600 mb-4" />
              <h3 className="text-xl font-light mb-2 dark:text-white">No posts yet</h3>
              <p className="text-gray-500 dark:text-gray-400 text-center">
                When you share photos and videos, they'll appear on your profile.
              </p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="saved" className="mt-0">
          {savedPosts.length > 0 ? (
            <div className="space-y-0 lg:space-y-6">
              {savedPosts.map((post) => (
                <PostCard 
                  key={post._id} 
                  post={post} 
                  onEdit={handleEditPost}
                  onDelete={handleDeletePost}
                  onSave={handleSavePost}
                  onAddComment={handleAddComment}
                  onLike={handleLike}
                />
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-16">
              <Bookmark className="h-16 w-16 text-gray-300 dark:text-gray-600 mb-4" />
              <h3 className="text-xl font-light mb-2 dark:text-white">No saved posts</h3>
              <p className="text-gray-500 dark:text-gray-400 text-center">
                Save posts to see them here
              </p>
            </div>
          )}
        </TabsContent>
      </Tabs>

      {/* Followers Modal */}
      <FollowersModal
        isOpen={showFollowersModal}
        onClose={() => setShowFollowersModal(false)}
        userId={displayUser?._id}
        type="followers"
      />

      {/* Following Modal */}
      <FollowersModal
        isOpen={showFollowingModal}
        onClose={() => setShowFollowingModal(false)}
        userId={displayUser?._id}
        type="following"
      />
    </div>
  );
};

export default Profile;
