import React, { useRef } from 'react';
import { ChevronLeft, ChevronRight, Plus } from 'lucide-react';
import { Button } from './ui/button';
import { mockStories } from '../data/mockData';
import { useAuth } from './AuthGuard';

const StoriesCarousel = () => {
  const scrollRef = useRef(null);
  const { user } = useAuth();

  const scroll = (direction) => {
    const { current } = scrollRef;
    if (direction === 'left') {
      current.scrollLeft -= 300;
    } else {
      current.scrollLeft += 300;
    }
  };

  // Create user's own story with their profile image
  const userStory = user ? {
    id: 'user-story',
    username: 'Your Story',
    avatar: user.avatar ? `http://localhost:5001/${user.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=150&h=150&fit=crop&crop=face',
    hasNewStory: false,
    isOwnStory: true
  } : null;

  // Combine user story with mock stories
  const allStories = userStory ? [userStory, ...mockStories] : mockStories;

  return (
    <div className="relative py-4 px-4">
      {/* Left arrow */}
      <Button
        variant="ghost"
        size="sm"
        className="absolute left-2 top-1/2 transform -translate-y-1/2 z-10 w-8 h-8 rounded-full bg-white shadow-md border hidden lg:flex items-center justify-center"
        onClick={() => scroll('left')}
      >
        <ChevronLeft className="h-4 w-4" />
      </Button>

      {/* Stories container */}
      <div
        ref={scrollRef}
        className="flex space-x-4 overflow-x-auto scrollbar-hide scroll-smooth"
        style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
      >
        {allStories.map((story) => (
          <div
            key={story.id}
            className="flex flex-col items-center space-y-1 min-w-[66px] cursor-pointer"
          >
            <div className={`w-16 h-16 rounded-full p-0.5 ${story.hasNewStory ? 'bg-gradient-to-tr from-yellow-400 via-red-500 to-purple-500' : 'bg-gray-300'}`}>
              <div className="w-full h-full rounded-full border-2 border-white overflow-hidden relative">
                <img
                  src={story.avatar}
                  alt={story.username}
                  className="w-full h-full object-cover"
                />
                {story.isOwnStory && (
                  <div className="absolute bottom-0 right-0 w-5 h-5 bg-blue-500 rounded-full border-2 border-white flex items-center justify-center">
                    <Plus className="h-3 w-3 text-white" />
                  </div>
                )}
              </div>
            </div>
            <span className="text-xs text-center text-gray-900 truncate w-full">
              {story.username}
            </span>
          </div>
        ))}
      </div>

      {/* Right arrow */}
      <Button
        variant="ghost"
        size="sm"
        className="absolute right-2 top-1/2 transform -translate-y-1/2 z-10 w-8 h-8 rounded-full bg-white shadow-md border hidden lg:flex items-center justify-center"
        onClick={() => scroll('right')}
      >
        <ChevronRight className="h-4 w-4" />
      </Button>
    </div>
  );
};

export default StoriesCarousel;
