import React, { useState, useEffect } from "react";
import { mockPosts } from "../data/mockData";
import Stories from "../components/Stories";

const Home = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    setPosts([...mockPosts]); // load mock posts
  }, []);

  return (
    <div className="max-w-lg mx-auto pt-2 pb-20">
      {/* ✅ Stories section */}
      <Stories />

      {/* ✅ Posts section */}
      {posts.map((post) => (
        <div
          key={post.id}
          className="bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-700 rounded-lg mb-6"
        >
          {/* Post header */}
          <div className="flex items-center p-3">
            <img
              src={post.userAvatar}
              alt={post.username}
              className="w-10 h-10 rounded-full mr-3"
            />
            <div>
              <p className="font-semibold dark:text-white">{post.username}</p>
              <p className="text-sm text-gray-500">{post.location}</p>
            </div>
          </div>

          {/* Post image */}
          <img src={post.imageUrl} alt="" className="w-full object-cover" />

          {/* Post details */}
          <div className="p-3">
            <p className="text-sm font-semibold dark:text-white">
              ❤️ {post.likes} likes
            </p>
            <p className="dark:text-white">
              <span className="font-semibold">{post.username} </span>
              {post.caption}
            </p>
            <p className="text-xs text-gray-500 mt-1">{post.timeAgo}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Home;
