import React, { useState, useEffect, useRef } from "react";
import { Send, Smile, Image, Heart, Search, ArrowLeft, RefreshCw } from "lucide-react";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { mockConversations, mockMessages } from "../data/mockData"; // ✅ add mock data imports



const Messages = () => {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [isRefreshing, setIsRefreshing] = useState(false);
  const messagesEndRef = useRef(null);
  const [showMobileChat, setShowMobileChat] = useState(false);

  // Simulate fetch conversations
  const fetchConversations = () => {
    setConversations([...mockConversations]);
  };

  // Simulate fetch messages
  const fetchMessages = (conversationId) => {
    const filtered = mockMessages.filter((m) => m.conversationId === conversationId);
    setMessages(filtered);
  };

  // Handle selecting a conversation
  const handleConversationSelect = (conversation) => {
    setSelectedConversation(conversation);
    fetchMessages(conversation.id);
    setShowMobileChat(true);
  };

  // Handle sending a message
  const handleSendMessage = () => {
    if (!newMessage.trim() || !selectedConversation) return;

    const newMsg = {
      id: Date.now(),
      conversationId: selectedConversation.id,
      text: newMessage,
      sender: "You",
      createdAt: new Date().toISOString(),
    };

    setMessages((prev) => [...prev, newMsg]);
    setNewMessage("");
  };

  // Refresh mock conversations (simulated)
  const refreshConversations = () => {
    setIsRefreshing(true);
    setTimeout(() => {
      fetchConversations();
      setIsRefreshing(false);
    }, 1000);
  };

  // Scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    fetchConversations();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Filter search
  const filteredConversations = conversations.filter((conv) =>
    conv.user.username.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatTime = (date) => {
    const diff = (new Date() - new Date(date)) / 1000 / 60;
    if (diff < 1) return "now";
    if (diff < 60) return `${Math.floor(diff)}m`;
    const diffH = diff / 60;
    if (diffH < 24) return `${Math.floor(diffH)}h`;
    return `${Math.floor(diffH / 24)}d`;
  };

  return (
    <div className="h-screen bg-white dark:bg-gray-900 flex">
      {/* Sidebar Conversations */}
      <div className={`w-full lg:w-80 border-r border-gray-300 dark:border-gray-700 flex flex-col ${showMobileChat ? "hidden lg:flex" : "flex"}`}>
        {/* Header */}
        <div className="p-4 border-b border-gray-300 dark:border-gray-700">
          <div className="flex items-center justify-between mb-3">
            <h1 className="text-xl font-semibold dark:text-white">Messages</h1>
            <Button
              variant="ghost"
              size="sm"
              onClick={refreshConversations}
              disabled={isRefreshing}
              className="text-gray-500 hover:text-gray-700"
            >
              <RefreshCw className={`h-4 w-4 ${isRefreshing ? "animate-spin" : ""}`} />
            </Button>
          </div>

          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400 dark:text-gray-500" />
            <Input
              type="text"
              placeholder="Search messages..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 bg-gray-50 dark:bg-gray-700 border-none rounded-lg dark:text-white"
            />
          </div>
        </div>

        {/* Conversation List */}
        <div className="flex-1 overflow-y-auto">
          {filteredConversations.length > 0 ? (
            filteredConversations.map((conv) => (
              <div
                key={conv.id}
                className={`flex items-center p-4 cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-700 border-b dark:border-gray-700 ${
                  selectedConversation?.id === conv.id ? "bg-gray-50 dark:bg-gray-700" : ""
                }`}
                onClick={() => handleConversationSelect(conv)}
              >
                <img
                  src={conv.user.avatar}
                  alt={conv.user.username}
                  className="w-12 h-12 rounded-full mr-3 object-cover"
                />
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <h3 className="font-semibold text-sm dark:text-white truncate">{conv.user.username}</h3>
                    <span className="text-xs text-gray-500">{formatTime(conv.lastMessage.createdAt)}</span>
                  </div>
                  <p className="text-sm text-gray-500 truncate">{conv.lastMessage.text}</p>
                </div>
              </div>
            ))
          ) : (
            <div className="flex-1 flex items-center justify-center text-gray-500 dark:text-gray-400">
              No conversations yet.
            </div>
          )}
        </div>
      </div>

      {/* Chat Area */}
      <div className={`flex-1 flex flex-col ${!showMobileChat ? "hidden lg:flex" : "flex"}`}>
        {selectedConversation ? (
          <>
            {/* Chat Header */}
            <div className="p-4 border-b border-gray-300 dark:border-gray-700 flex items-center">
              <Button
                variant="ghost"
                size="sm"
                className="lg:hidden mr-3"
                onClick={() => setShowMobileChat(false)}
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <img
                src={selectedConversation.user.avatar}
                alt={selectedConversation.user.username}
                className="w-10 h-10 rounded-full mr-3"
              />
              <div>
                <h3 className="font-semibold dark:text-white">{selectedConversation.user.username}</h3>
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {messages.map((msg) => (
                <div
                  key={msg.id}
                  className={`flex ${msg.sender === "You" ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`max-w-xs px-4 py-2 rounded-2xl ${
                      msg.sender === "You"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-100 dark:bg-gray-700 text-black dark:text-white"
                    }`}
                  >
                    <p className="text-sm">{msg.text}</p>
                    <p className="text-xs mt-1 text-gray-300">{formatTime(msg.createdAt)}</p>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="p-4 border-t border-gray-300 dark:border-gray-700">
              <div className="flex items-center space-x-2">
                <Button variant="ghost" size="sm" className="p-2">
                  <Image className="h-5 w-5" />
                </Button>
                <div className="flex-1">
                  <Input
                    type="text"
                    placeholder="Message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && handleSendMessage()}
                    className="border-none bg-gray-100 dark:bg-gray-700 rounded-full px-4 focus:ring-1 focus:ring-gray-300 dark:text-white"
                  />
                </div>
                <Button variant="ghost" size="sm" className="p-2">
                  <Smile className="h-5 w-5" />
                </Button>
                {newMessage.trim() ? (
                  <Button
                    onClick={handleSendMessage}
                    className="bg-blue-500 hover:bg-blue-600 rounded-full px-4"
                  >
                    <Send className="h-4 w-4" />
                  </Button>
                ) : (
                  <Button variant="ghost" size="sm" className="p-2">
                    <Heart className="h-5 w-5" />
                  </Button>
                )}
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-500 dark:text-gray-400">
            Select a conversation to start chatting.
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
