import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Send, Smile, Image, Heart, Search, ArrowLeft } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { useAuth } from '../components/AuthGuard';
import { useSearchParams } from 'react-router-dom';

const Messages = () => {
  const { user } = useAuth();
  const [searchParams] = useSearchParams();
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const [showMobileChat, setShowMobileChat] = useState(false);

  // Fetch conversations
  const fetchConversations = async () => {
    try {
      const response = await axios.get(`/api/messages/conversations/${user._id}`);
      setConversations(response.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  // Fetch messages for selected conversation
  const fetchMessages = async (otherUserId) => {
    try {
      setIsLoading(true);
      const response = await axios.get(`/api/messages/${user._id}/${otherUserId}`);
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Send message
  const handleSendMessage = async () => {
    if (newMessage.trim() && selectedConversation) {
      try {
        const response = await axios.post('/api/messages', {
          sender: user._id,
          receiver: selectedConversation.user._id,
          text: newMessage.trim()
        });

        // Add message to local state
        setMessages(prev => [...prev, response.data]);
        setNewMessage('');

        // Refresh conversations to update last message
        fetchConversations();

        // Refresh notification count
        window.dispatchEvent(new CustomEvent('refreshNotifications'));
      } catch (error) {
        console.error('Error sending message:', error);
        alert('Failed to send message');
      }
    }
  };

  // Handle conversation selection
  const handleConversationSelect = (conversation) => {
    setSelectedConversation(conversation);
    fetchMessages(conversation.user._id);
    setShowMobileChat(true);
  };

  // Scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    if (user?._id) {
      fetchConversations();
    }
  }, [user?._id]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Filter conversations based on search
  const filteredConversations = conversations.filter(conv =>
    conv.user.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
    conv.user.displayName?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatTime = (date) => {
    const now = new Date();
    const messageDate = new Date(date);
    const diffInHours = Math.floor((now - messageDate) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      return 'now';
    } else if (diffInHours < 24) {
      return `${diffInHours}h`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays}d`;
    }
  };

  return (
    <div className="h-screen bg-white flex">
      {/* Conversations List */}
      <div className={`w-full lg:w-80 border-r border-gray-300 flex flex-col ${showMobileChat ? 'hidden lg:flex' : 'flex'}`}>
        {/* Header */}
        <div className="p-4 border-b border-gray-300">
          <div className="flex items-center justify-between mb-3">
            <h1 className="text-xl font-semibold">Messages</h1>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => {
                // Navigate to search page to find users to message
                window.location.href = '/search';
              }}
              className="text-blue-500 hover:text-blue-600"
            >
              New Message
            </Button>
          </div>

          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              type="text"
              placeholder="Search messages..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 bg-gray-50 border-none rounded-lg"
            />
          </div>
        </div>

        {/* Conversations List */}
        <div className="flex-1 overflow-y-auto">
          {filteredConversations.length > 0 ? (
            filteredConversations.map((conversation) => (
              <div
                key={conversation.user._id}
                className={`flex items-center p-4 hover:bg-gray-50 cursor-pointer border-b border-gray-100 ${
                  selectedConversation?.user._id === conversation.user._id ? 'bg-gray-50' : ''
                }`}
                onClick={() => handleConversationSelect(conversation)}
              >
                <div className="relative mr-3">
                  <div className="w-12 h-12 rounded-full overflow-hidden">
                    <img
                      src={conversation.user.avatar ? `http://localhost:5001/${conversation.user.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=100&h=100&fit=crop&crop=face'}
                      alt={conversation.user.username}
                      className="w-full h-full object-cover"
                    />
                  </div>
                  {conversation.unreadCount > 0 && (
                    <div className="absolute -top-1 -right-1 w-5 h-5 bg-blue-500 text-white text-xs rounded-full flex items-center justify-center">
                      {conversation.unreadCount}
                    </div>
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <h3 className="font-semibold text-sm truncate">
                      {conversation.user.displayName || conversation.user.username}
                    </h3>
                    <span className="text-xs text-gray-500">
                      {formatTime(conversation.lastMessage.createdAt)}
                    </span>
                  </div>
                  <p className={`text-sm truncate ${
                    conversation.unreadCount > 0 ? 'font-semibold text-black' : 'text-gray-500'
                  }`}>
                    {conversation.lastMessage.text}
                  </p>
                </div>
              </div>
            ))
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-4 rounded-full border-2 border-gray-300 flex items-center justify-center">
                  <Send className="h-8 w-8 text-gray-300" />
                </div>
                <h3 className="text-lg font-light mb-2">No conversations yet</h3>
                <p className="text-gray-500 text-sm">Start a conversation with someone!</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Chat Window */}
      <div className={`flex-1 flex flex-col ${!showMobileChat ? 'hidden lg:flex' : 'flex'}`}>
        {selectedConversation ? (
          <>
            {/* Chat Header */}
            <div className="p-4 border-b border-gray-300 flex items-center">
              <Button
                variant="ghost"
                size="sm"
                className="lg:hidden mr-3"
                onClick={() => setShowMobileChat(false)}
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <div className="relative mr-3">
                <div className="w-10 h-10 rounded-full overflow-hidden">
                  <img
                    src={selectedConversation.user.avatar ? `http://localhost:5001/${selectedConversation.user.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=100&h=100&fit=crop&crop=face'}
                    alt={selectedConversation.user.username}
                    className="w-full h-full object-cover"
                  />
                </div>
              </div>
              <div>
                <h3 className="font-semibold">
                  {selectedConversation.user.displayName || selectedConversation.user.username}
                </h3>
                <p className="text-xs text-gray-500">@{selectedConversation.user.username}</p>
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {isLoading ? (
                <div className="flex justify-center">
                  <div className="text-gray-500">Loading messages...</div>
                </div>
              ) : messages.length > 0 ? (
                messages.map((message) => {
                  const isOwn = message.sender._id === user._id;
                  return (
                    <div
                      key={message._id}
                      className={`flex ${isOwn ? 'justify-end' : 'justify-start'}`}
                    >
                      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl ${
                        isOwn 
                          ? 'bg-blue-500 text-white' 
                          : 'bg-gray-100 text-black'
                      }`}>
                        <p className="text-sm">{message.text}</p>
                        <p className={`text-xs mt-1 ${
                          isOwn ? 'text-blue-100' : 'text-gray-500'
                        }`}>
                          {formatTime(message.createdAt)}
                        </p>
                      </div>
                    </div>
                  );
                })
              ) : (
                <div className="flex-1 flex items-center justify-center">
                  <div className="text-center">
                    <div className="w-16 h-16 mx-auto mb-4 rounded-full border-2 border-gray-300 flex items-center justify-center">
                      <Send className="h-8 w-8 text-gray-300" />
                    </div>
                    <h3 className="text-lg font-light mb-2">No messages yet</h3>
                    <p className="text-gray-500 text-sm">Start the conversation!</p>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input */}
            <div className="p-4 border-t border-gray-300">
              <div className="flex items-center space-x-2">
                <Button variant="ghost" size="sm" className="p-2">
                  <Image className="h-5 w-5" />
                </Button>
                <div className="flex-1">
                  <Input
                    type="text"
                    placeholder="Message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    className="border-none bg-gray-100 rounded-full px-4 focus:ring-1 focus:ring-gray-300"
                  />
                </div>
                <Button variant="ghost" size="sm" className="p-2">
                  <Smile className="h-5 w-5" />
                </Button>
                {newMessage.trim() ? (
                  <Button
                    onClick={handleSendMessage}
                    className="bg-blue-500 hover:bg-blue-600 rounded-full px-4"
                  >
                    <Send className="h-4 w-4" />
                  </Button>
                ) : (
                  <Button variant="ghost" size="sm" className="p-2">
                    <Heart className="h-5 w-5" />
                  </Button>
                )}
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <div className="w-24 h-24 mx-auto mb-4 rounded-full border-2 border-gray-300 flex items-center justify-center">
                <Send className="h-12 w-12 text-gray-300" />
              </div>
              <h3 className="text-xl font-light mb-2">Your Messages</h3>
              <p className="text-gray-500">Send private photos and messages to a friend or group.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
