import React, { useState, useEffect } from 'react';
import { Heart, UserPlus, MessageCircle, X } from 'lucide-react';
import { Button } from '../components/ui/button';
import { useAuth } from '../components/AuthGuard';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [unreadCount, setUnreadCount] = useState(0);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (user?._id) {
      fetchNotifications();
      fetchUnreadCount();
    }
  }, [user?._id]);

  const fetchNotifications = async () => {
    try {
      const response = await axios.get(`/api/notifications/${user._id}`);
      setNotifications(response.data);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUnreadCount = async () => {
    try {
      const response = await axios.get(`/api/notifications/${user._id}/unread-count`);
      setUnreadCount(response.data.count);
    } catch (error) {
      console.error('Error fetching unread count:', error);
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      await axios.put(`/api/notifications/${notificationId}/read`);
      setNotifications(prev => 
        prev.map(notif => 
          notif._id === notificationId ? { ...notif, read: true } : notif
        )
      );
      setUnreadCount(prev => Math.max(0, prev - 1));

      // Refresh notification count in navigation
      window.dispatchEvent(new CustomEvent('refreshNotifications'));
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await axios.put(`/api/notifications/user/${user._id}/read-all`);
      setNotifications(prev => 
        prev.map(notif => ({ ...notif, read: true }))
      );
      setUnreadCount(0);

      // Refresh notification count in navigation
      window.dispatchEvent(new CustomEvent('refreshNotifications'));
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
    }
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'like':
        return <Heart className="h-5 w-5 text-red-500" />;
      case 'follow':
        return <UserPlus className="h-5 w-5 text-blue-500" />;
      case 'comment':
        return <MessageCircle className="h-5 w-5 text-green-500" />;
      case 'message':
        return <MessageCircle className="h-5 w-5 text-purple-500" />;
      default:
        return <Heart className="h-5 w-5 text-gray-500" />;
    }
  };

  const getNotificationText = (notification) => {
    const { from, type } = notification;
    const username = from?.username || 'Someone';

    switch (type) {
      case 'like':
        return `${username} liked your post`;
      case 'follow':
        return `${username} started following you`;
      case 'comment':
        return `${username} commented on your post`;
      case 'message':
        return `${username} sent you a message`;
      default:
        return 'New notification';
    }
  };

  const handleNotificationClick = async (notification) => {
    if (!notification.read) {
      await markAsRead(notification._id);
    }

    if (notification.type === 'follow') {
      navigate(`/profile/${notification.from?.username}`);
    } else if (notification.type === 'message') {
      navigate(`/messages?user=${notification.from?._id}&username=${notification.from?.username}`);
    } else if (notification.post) {
      // Navigate to the post (you might want to implement a post detail page)
      navigate('/home');
    }
  };

  const formatTimeAgo = (date) => {
    const now = new Date();
    const notificationDate = new Date(date);
    const diffInSeconds = Math.floor((now - notificationDate) / 1000);

    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h ago`;
    if (diffInSeconds < 2592000) return `${Math.floor(diffInSeconds / 86400)}d ago`;
    return `${Math.floor(diffInSeconds / 2592000)}mo ago`;
  };

  if (loading) {
    return (
      <div className="max-w-2xl mx-auto p-4">
        <div className="flex justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-4">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold text-gray-900">Notifications</h1>
        {unreadCount > 0 && (
          <Button
            variant="outline"
            size="sm"
            onClick={markAllAsRead}
          >
            Mark all as read
          </Button>
        )}
      </div>

      {/* Notifications List */}
      {notifications.length > 0 ? (
        <div className="space-y-2">
          {notifications.map((notification) => (
            <div
              key={notification._id}
              className={`flex items-start space-x-3 p-4 rounded-lg cursor-pointer transition-colors border-l-4 ${
                notification.read 
                  ? 'bg-white hover:bg-gray-50 border-l-transparent' 
                  : 'bg-blue-50 hover:bg-blue-100 border-l-blue-500'
              }`}
              onClick={() => handleNotificationClick(notification)}
            >
              {/* Icon */}
              <div className="flex-shrink-0 mt-1">
                {getNotificationIcon(notification.type)}
              </div>

              {/* User Avatar */}
              <div className="flex-shrink-0">
                <div className="w-10 h-10 rounded-full overflow-hidden">
                  <img
                    src={notification.from?.avatar ? `http://localhost:5001/${notification.from.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=150&h=150&fit=crop&crop=face'}
                    alt={notification.from?.username}
                    className="w-full h-full object-cover"
                  />
                </div>
              </div>

              {/* Content */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2">
                  <p className="text-sm text-gray-900">
                    {getNotificationText(notification)}
                  </p>
                  {!notification.read && (
                    <div className="w-2 h-2 bg-blue-500 rounded-full flex-shrink-0"></div>
                  )}
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  {formatTimeAgo(notification.createdAt)}
                </p>
              </div>

              {/* Post Preview (for like/comment notifications) */}
              {notification.post && (
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 rounded-lg overflow-hidden">
                    <img
                      src={notification.post.photo ? `http://localhost:5001/${notification.post.photo}` : 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=600&fit=crop'}
                      alt="Post"
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
              )}

              {/* Unread indicator */}
              {!notification.read && (
                <div className="flex-shrink-0">
                  <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <Heart className="h-16 w-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">No notifications yet</h3>
          <p className="text-gray-600">
            When someone likes your post or follows you, you'll see it here.
          </p>
        </div>
      )}
    </div>
  );
};

export default Notifications;
