import React, { useState } from 'react';
import { Heart, MessageCircle, Send, Bookmark, MoreHorizontal, Edit, Trash2 } from 'lucide-react';
import { Button } from './ui/button';
import { useAuth } from './AuthGuard';

const PostCard = ({ post, onEdit, onDelete, onSave }) => {
  const [liked, setLiked] = useState(false);
  const [saved, setSaved] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [showOptions, setShowOptions] = useState(false);
  const { user } = useAuth();

  const handleDoubleClick = () => {
    setLiked(true);
  };

  const handleSave = () => {
    setSaved(!saved);
    if (onSave) {
      onSave(post._id, !saved);
    }
  };

  const handleEdit = () => {
    if (onEdit) {
      onEdit(post);
    }
    setShowOptions(false);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      if (onDelete) {
        onDelete(post._id);
      }
    }
    setShowOptions(false);
  };

  const isOwner = user && post.user && (user._id === post.user._id || user.username === post.user.username);

  // Helper function to format time ago
  const getTimeAgo = (date) => {
    const now = new Date();
    const postDate = new Date(date);
    const diffInSeconds = Math.floor((now - postDate) / 1000);

    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h`;
    if (diffInSeconds < 2592000) return `${Math.floor(diffInSeconds / 86400)}d`;
    return `${Math.floor(diffInSeconds / 2592000)}mo`;
  };

  return (
    <article className="bg-white border-b border-gray-300 lg:border lg:rounded-lg lg:mb-6">
      {/* Header */}
      <header className="flex items-center justify-between p-4">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 rounded-full overflow-hidden">
            <img
              src={post.user?.avatar ? `http://localhost:5001/${post.user.avatar}` : 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=100&h=100&fit=crop&crop=face'}
              alt={post.user?.username}
              className="w-full h-full object-cover"
            />
          </div>
          <div>
            <h3 className="font-semibold text-sm">{post.user?.username}</h3>
            {post.location && (
              <p className="text-xs text-gray-500">{post.location}</p>
            )}
          </div>
        </div>
        <Button variant="ghost" size="sm" className="p-1">
          <MoreHorizontal className="h-5 w-5" />
        </Button>
      </header>

      {/* Media */}
      <div className="relative aspect-square bg-gray-100" onDoubleClick={handleDoubleClick}>
        <img
          src={post.photo ? `http://localhost:5001/${post.photo}` : 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=600&fit=crop'}
          alt="Post"
          className="w-full h-full object-cover"
        />
        {/* Double tap heart animation */}
        {liked && (
          <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
            <Heart className="h-20 w-20 text-white fill-current animate-ping" />
          </div>
        )}
      </div>

      {/* Actions */}
      <div className="flex items-center justify-between p-4">
        <div className="flex items-center space-x-4">
          <Button
            variant="ghost"
            size="sm"
            className="p-0 hover:opacity-50 transition-opacity"
            onClick={() => setLiked(!liked)}
          >
            <Heart className={`h-6 w-6 ${liked ? 'fill-red-500 text-red-500' : 'text-black'}`} />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            className="p-0 hover:opacity-50 transition-opacity"
            onClick={() => setShowComments(!showComments)}
          >
            <MessageCircle className="h-6 w-6" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            className="p-0 hover:opacity-50 transition-opacity"
          >
            <Send className="h-6 w-6" />
          </Button>
        </div>
        <Button
          variant="ghost"
          size="sm"
          className="p-0 hover:opacity-50 transition-opacity"
          onClick={() => setSaved(!saved)}
        >
          <Bookmark className={`h-6 w-6 ${saved ? 'fill-current' : ''}`} />
        </Button>
      </div>

      {/* Likes */}
      <div className="px-4 pb-2">
        <p className="font-semibold text-sm">
          {(post.likes?.length || 0) + (liked ? 1 : 0)} likes
        </p>
      </div>

      {/* Caption */}
      <div className="px-4 pb-2">
        <p className="text-sm">
          <span className="font-semibold mr-2">{post.user?.username}</span>
          {post.caption}
        </p>
      </div>

      {/* Comments */}
      <div className="px-4 pb-2">
        {post.comments?.length > 0 && (
          <Button
            variant="ghost"
            className="p-0 h-auto text-sm text-gray-500 hover:bg-transparent"
            onClick={() => setShowComments(!showComments)}
          >
            View all {post.comments.length} comments
          </Button>
        )}

        {showComments && post.comments && (
          <div className="mt-2 space-y-1">
            {post.comments.slice(0, 3).map((comment, index) => (
              <p key={index} className="text-sm">
                <span className="font-semibold mr-2">{comment.user?.username || 'Unknown'}</span>
                {comment.text}
              </p>
            ))}
          </div>
        )}
      </div>

      {/* Time */}
      <div className="px-4 pb-4">
        <p className="text-xs text-gray-500 uppercase">{getTimeAgo(post.createdAt)}</p>
      </div>

      {/* Add comment */}
      <div className="border-t border-gray-300 px-4 py-3">
        <div className="flex items-center space-x-3">
          <input
            type="text"
            placeholder="Add a comment..."
            className="flex-1 text-sm border-none outline-none bg-transparent"
          />
          <Button
            variant="ghost"
            className="p-0 h-auto text-sm font-semibold text-blue-500 hover:bg-transparent"
          >
            Post
          </Button>
        </div>
      </div>
    </article>
  );
};

export default PostCard;
