import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import {
  ArrowLeft, Image, Video, Smile,
  Play, Pause, Volume2, VolumeX
} from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Textarea } from '../components/ui/textarea';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthGuard';
import { usePosts } from '../contexts/PostContext'; // ✅ Import Post context

const CreatePost = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const { user } = useAuth();
  const { addPost } = usePosts(); // ✅ Add post to global state

  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [caption, setCaption] = useState('');
  const [location, setLocation] = useState('');
  const [step, setStep] = useState('select');
  const [postType, setPostType] = useState('photo');
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [videoDuration, setVideoDuration] = useState(0);
  const [videoCurrentTime, setVideoCurrentTime] = useState(0);

  const handleFileSelect = (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const isVideo = file.type.startsWith('video/');
    const isImage = file.type.startsWith('image/');

    if ((postType === 'reel' && !isVideo) || (postType === 'photo' && !isImage)) {
      alert(`Please select a ${postType === 'reel' ? 'video' : 'photo'} file for this post type.`);
      return;
    }

    setSelectedFile(file);
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);
    setPostType(isVideo ? 'reel' : 'photo');
    setStep('edit');
  };

  const handlePostTypeSelect = (type) => {
    setPostType(type);
    setSelectedFile(null);
    setPreviewUrl(null);
    setStep('select');
  };

  const handleVideoLoad = () => {
    if (videoRef.current) setVideoDuration(videoRef.current.duration);
  };

  const handleVideoTimeUpdate = () => {
    if (videoRef.current) setVideoCurrentTime(videoRef.current.currentTime);
  };

  const toggleVideoPlay = () => {
    if (!videoRef.current) return;
    if (isVideoPlaying) {
      videoRef.current.pause();
    } else {
      videoRef.current.play();
    }
    setIsVideoPlaying(!isVideoPlaying);
  };

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  const handleVideoEnd = () => {
    setIsVideoPlaying(false);
    if (videoRef.current) videoRef.current.currentTime = 0;
  };

  const handleShare = async () => {
    try {
      if (!selectedFile) {
        alert('Please select a file first.');
        return;
      }

      // If you have backend running, use axios upload
      let newPost;
      if (user && user._id) {
        const form = new FormData();
        form.append('photo', selectedFile);
        form.append('caption', caption);
        form.append('location', location);
        form.append('user', user._id);
        form.append('postType', postType);

        const res = await axios.post('/api/posts', form);
        newPost = res.data;
      } else {
        // Fallback: simulate a new local post (mock data)
        newPost = {
          id: Date.now(),
          username: user?.username || 'guest',
          userAvatar: user?.avatar || '/images/default-avatar.jpg',
          imageUrl: previewUrl,
          location,
          likes: 0,
          caption,
          timeAgo: 'Just now',
          comments: []
        };
      }

      // ✅ Add to global posts context
      addPost(newPost);

      // ✅ Navigate to profile or home
      navigate(`/profile/${user?.username || 'guest'}`);
    } catch (error) {
      console.error('Error creating post:', error);
      alert('Failed to create post. Please try again.');
    }
  };

  const handleBack = () => {
    if (step === 'edit') {
      setStep('select');
      setSelectedFile(null);
      setPreviewUrl(null);
      setPostType('photo');
      setIsVideoPlaying(false);
      setIsMuted(false);
    } else if (step === 'share') {
      setStep('edit');
    } else {
      navigate('/home');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  useEffect(() => {
    return () => previewUrl && URL.revokeObjectURL(previewUrl);
  }, [previewUrl]);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      {/* Header */}
      <div className="sticky top-0 bg-white dark:bg-gray-800 border-b border-gray-300 dark:border-gray-700 z-10">
        <div className="flex items-center justify-between p-4">
          <Button variant="ghost" onClick={handleBack}>
            <ArrowLeft className="h-6 w-6" />
          </Button>
          <h1 className="font-semibold dark:text-white">Create new post</h1>
          {step === 'edit' && (
            <Button variant="ghost" className="text-blue-500 font-semibold" onClick={() => setStep('share')}>
              Next
            </Button>
          )}
          {step === 'share' && (
            <Button variant="ghost" className="text-blue-500 font-semibold" onClick={handleShare}>
              Share
            </Button>
          )}
          {step === 'select' && <div className="w-16" />}
        </div>
      </div>

      {/* Step 1: Select file */}
      {step === 'select' && (
        <div className="flex flex-col items-center justify-center h-full py-20">
          <div className="text-center mb-8">
            <div className="w-24 h-24 mx-auto mb-4 rounded-full bg-gray-100 dark:bg-gray-700 flex items-center justify-center">
              {postType === 'reel' ? (
                <Video className="h-12 w-12 text-gray-400 dark:text-gray-500" />
              ) : (
                <Image className="h-12 w-12 text-gray-400 dark:text-gray-500" />
              )}
            </div>
            <h2 className="text-xl font-light mb-2 dark:text-white">
              {postType === 'reel' ? 'Share a reel' : 'Share a photo'}
            </h2>
            <p className="text-gray-500 dark:text-gray-400">
              {postType === 'reel'
                ? "When you share videos, they'll appear on your profile as reels."
                : "When you share photos, they'll appear on your profile."}
            </p>
          </div>

          <Button onClick={() => fileInputRef.current?.click()} className="bg-blue-500 hover:bg-blue-600">
            {postType === 'reel' ? 'Select video from computer' : 'Select photo from computer'}
          </Button>

          <input
            ref={fileInputRef}
            type="file"
            accept={postType === 'reel' ? 'video/*' : 'image/*'}
            onChange={handleFileSelect}
            className="hidden"
          />

          <div className="mt-8 flex space-x-4">
            <Button variant={postType === 'photo' ? 'default' : 'outline'} size="sm" onClick={() => handlePostTypeSelect('photo')}>
              <Image className="h-4 w-4 mr-2" /> Photo
            </Button>
            <Button variant={postType === 'reel' ? 'default' : 'outline'} size="sm" onClick={() => handlePostTypeSelect('reel')}>
              <Video className="h-4 w-4 mr-2" /> Reel
            </Button>
          </div>
        </div>
      )}

      {/* Step 2: Edit preview */}
      {step === 'edit' && previewUrl && (
        <div className="max-w-lg mx-auto">
          <div className={`${postType === 'reel' ? 'aspect-[9/16]' : 'aspect-square'} bg-gray-100 dark:bg-gray-800 mb-4 relative`}>
            {postType === 'reel' ? (
              <div className="relative w-full h-full">
                <video
                  ref={videoRef}
                  src={previewUrl}
                  className="w-full h-full object-cover"
                  onLoadedMetadata={handleVideoLoad}
                  onTimeUpdate={handleVideoTimeUpdate}
                  onEnded={handleVideoEnd}
                  muted={isMuted}
                  loop
                  playsInline
                />
                <div className="absolute inset-0 flex items-center justify-center">
                  <Button variant="ghost" size="lg" onClick={toggleVideoPlay}>
                    {isVideoPlaying ? <Pause /> : <Play />}
                  </Button>
                </div>
              </div>
            ) : (
              <img src={previewUrl} alt="Preview" className="w-full h-full object-cover" />
            )}
          </div>
          <div className="flex justify-between mt-6">
            <Button variant="outline" onClick={() => setStep('select')}>
              Back
            </Button>
            <Button onClick={() => setStep('share')} className="bg-blue-500 hover:bg-blue-600">
              Next
            </Button>
          </div>
        </div>
      )}

      {/* Step 3: Share post */}
      {step === 'share' && previewUrl && (
        <div className="max-w-lg mx-auto p-4">
          <div className="flex space-x-4 mb-6">
            <div className={`${postType === 'reel' ? 'w-16 h-28' : 'w-16 h-16'} rounded-lg overflow-hidden`}>
              {postType === 'reel' ? (
                <video src={previewUrl} className="w-full h-full object-cover" muted loop autoPlay playsInline />
              ) : (
                <img src={previewUrl} alt="Preview" className="w-full h-full object-cover" />
              )}
            </div>
            <div className="flex-1">
              <Textarea
                placeholder="Write a caption..."
                value={caption}
                onChange={(e) => setCaption(e.target.value)}
                className="border-none resize-none focus:ring-0 text-base dark:text-white dark:bg-transparent"
                rows={3}
              />
              <div className="text-xs text-gray-500 mt-1">{caption.length}/2,200</div>
            </div>
          </div>

          <div className="mb-4">
            <Input
              type="text"
              placeholder="Add location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="w-full dark:text-white dark:bg-gray-800"
            />
          </div>

          <div className="flex justify-between">
            <Button variant="outline" onClick={() => setStep('edit')}>
              Back
            </Button>
            <Button onClick={handleShare} className="bg-blue-500 hover:bg-blue-600">
              Share
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
