import React, { useState, useEffect, useRef } from 'react';
import { Search, Video, Image, X, Play, Pause, Volume2, VolumeX } from 'lucide-react';
import { Input } from '../components/ui/input';
import { Button } from '../components/ui/button';
import { mockExplorePhotos } from '../data/mockData';
import axios from 'axios';
import PostCard from '../components/PostCard';

const Explore = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('all'); // all, photos, reels
  const [selectedPost, setSelectedPost] = useState(null);
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [videoDuration, setVideoDuration] = useState(0);
  const [videoCurrentTime, setVideoCurrentTime] = useState(0);
  const videoRef = useRef(null);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredPosts = posts.filter(post => {
    if (activeTab === 'photos') return post.postType === 'photo';
    if (activeTab === 'reels') return post.postType === 'reel';
    return true; // all
  });

  const handleLike = async (postId, isLiked) => {
    try {
      if (isLiked) {
        await axios.post(`/api/posts/${postId}/like`, { userId: localStorage.getItem('userId') });
      } else {
        await axios.delete(`/api/posts/${postId}/like`, { data: { userId: localStorage.getItem('userId') } });
      }
      // Refresh posts
      fetchPosts();
    } catch (error) {
      console.error('Error toggling like:', error);
    }
  };

  const handleAddComment = async (postId, text) => {
    try {
      await axios.post(`/api/posts/${postId}/comment`, {
        userId: localStorage.getItem('userId'),
        text
      });
      // Refresh posts
      fetchPosts();
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleSave = async (postId, isSaved) => {
    try {
      if (isSaved) {
        await axios.post(`/api/posts/${postId}/save`, { userId: localStorage.getItem('userId') });
      } else {
        await axios.delete(`/api/posts/${postId}/save`, { data: { userId: localStorage.getItem('userId') } });
      }
    } catch (error) {
      console.error('Error toggling save:', error);
    }
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center justify-center h-64">
          <div className="text-gray-500">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      {/* Search bar */}
      <div className="p-4 bg-white border-b border-gray-300 lg:hidden">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500" />
          <Input
            type="text"
            placeholder="Search"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 pr-4 py-2 w-full bg-gray-100 border-none rounded-lg focus:bg-white focus:ring-1 focus:ring-gray-300"
          />
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="flex border-b border-gray-300 bg-white">
        <button
          className={`flex-1 py-3 px-4 text-center font-medium ${
            activeTab === 'all' 
              ? 'text-blue-500 border-b-2 border-blue-500' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
          onClick={() => setActiveTab('all')}
        >
          All
        </button>
        <button
          className={`flex-1 py-3 px-4 text-center font-medium flex items-center justify-center gap-2 ${
            activeTab === 'photos' 
              ? 'text-blue-500 border-b-2 border-blue-500' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
          onClick={() => setActiveTab('photos')}
        >
          <Image className="h-4 w-4" />
          Photos
        </button>
        <button
          className={`flex-1 py-3 px-4 text-center font-medium flex items-center justify-center gap-2 ${
            activeTab === 'reels' 
              ? 'text-blue-500 border-b-2 border-blue-500' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
          onClick={() => setActiveTab('reels')}
        >
          <Video className="h-4 w-4" />
          Reels
        </button>
      </div>

      {/* Posts Grid/List */}
      {activeTab === 'all' ? (
        <div className="space-y-0">
          {filteredPosts.map((post) => (
            <PostCard
              key={post._id}
              post={post}
              onLike={handleLike}
              onAddComment={handleAddComment}
              onSave={handleSave}
            />
          ))}
        </div>
      ) : (
        <div className="grid grid-cols-3 gap-1 lg:gap-4 lg:p-4">
          {filteredPosts.map((post) => (
            <div
              key={post._id}
              className={`${post.postType === 'reel' ? 'aspect-[9/16]' : 'aspect-square'} bg-gray-100 cursor-pointer hover:opacity-90 transition-opacity relative`}
            >
              {post.postType === 'reel' ? (
                <div className="relative w-full h-full">
                  <video
                    src={post.video ? `http://localhost:5001/${post.video}` : ''}
                    className="w-full h-full object-cover"
                    muted
                    loop
                  />
                  <div className="absolute top-2 right-2 bg-black bg-opacity-50 text-white px-2 py-1 rounded text-xs font-semibold">
                    REEL
                  </div>
                  <div className="absolute bottom-2 left-2 text-white text-sm">
                    <Video className="h-4 w-4 inline mr-1" />
                  </div>
                </div>
              ) : (
                <img
                  src={post.photo ? `http://localhost:5001/${post.photo}` : 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=600&fit=crop'}
                  alt="Post"
                  className="w-full h-full object-cover"
                />
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Explore;
