import React, { useState } from 'react';
import { Outlet, useLocation } from 'react-router-dom';
import TopNavigation from './TopNavigation';
import BottomNavigation from './BottomNavigation';
import Sidebar from './Sidebar';
import StoriesCarousel from './StoriesCarousel';
import PostCard from './PostCard';
import { mockPosts } from '../data/mockData';

const Layout = () => {
  const location = useLocation();
  const [searchFocused, setSearchFocused] = useState(false);

  // Show posts & stories only on home route
  const isHome = location.pathname === '/';

  return (
    <div className="min-h-screen bg-white">
      {/* Top navigation */}
      <TopNavigation searchFocused={searchFocused} setSearchFocused={setSearchFocused} />

      <div className="flex">
        {/* Sidebar for large screens */}
        <div className="hidden lg:flex">
          <Sidebar />
        </div>

        {/* Main content */}
        <main className="flex-1 pt-[60px] lg:pt-0 lg:ml-64 pb-[50px] lg:pb-0 overflow-y-auto flex flex-col items-center">
          {isHome && (
            <>
              {/* Stories carousel */}
              <StoriesCarousel />

              {/* Posts */}
              {mockPosts.map((post) => (
                <div key={post.id} className="w-full max-w-[600px]">
                  <PostCard post={post} />
                </div>
              ))}
            </>
          )}

          {/* Render other pages based on route */}
          <Outlet />
        </main>
      </div>

      {/* Bottom navigation for mobile */}
      <div className="lg:hidden fixed bottom-0 left-0 right-0 z-50">
        <BottomNavigation />
      </div>
    </div>
  );
};

export default Layout;
