import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ArrowLeft, Image, Video, Smile, Play, Pause, Volume2, VolumeX } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Textarea } from '../components/ui/textarea';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthGuard';

const CreatePost = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const { user } = useAuth();
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [caption, setCaption] = useState('');
  const [location, setLocation] = useState('');
  const [step, setStep] = useState('select'); // select, edit, share
  const [postType, setPostType] = useState('photo'); // photo, reel
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [videoDuration, setVideoDuration] = useState(0);
  const [videoCurrentTime, setVideoCurrentTime] = useState(0);

  const handleFileSelect = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check if file type matches selected post type
      const isVideo = file.type.startsWith('video/');
      const isImage = file.type.startsWith('image/');

      if ((postType === 'reel' && !isVideo) || (postType === 'photo' && !isImage)) {
        alert(`Please select a ${postType === 'reel' ? 'video' : 'photo'} file for a ${postType}.`);
        return;
      }

      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);

      // Auto-detect post type based on file type
      if (isVideo) {
        setPostType('reel');
      } else if (isImage) {
        setPostType('photo');
      }

      setStep('edit');
    }
  };

  const handlePostTypeSelect = (type) => {
    setPostType(type);
    // Clear any selected file when switching post types
    if (selectedFile) {
      setSelectedFile(null);
      setPreviewUrl(null);
      setStep('select');
    }
  };

  const handleVideoLoad = () => {
    if (videoRef.current) {
      setVideoDuration(videoRef.current.duration);
    }
  };

  const handleVideoTimeUpdate = () => {
    if (videoRef.current) {
      setVideoCurrentTime(videoRef.current.currentTime);
    }
  };

  const toggleVideoPlay = () => {
    if (videoRef.current) {
      if (isVideoPlaying) {
        videoRef.current.pause();
      } else {
        // Ensure video is not muted when user plays it
        videoRef.current.muted = false;
        setIsMuted(false);
        videoRef.current.play();
      }
      setIsVideoPlaying(!isVideoPlaying);
    }
  };

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  const handleVideoEnd = () => {
    setIsVideoPlaying(false);
    if (videoRef.current) {
      videoRef.current.currentTime = 0;
    }
  };

  const handleShare = async () => {
    console.log('=== POST CREATION DEBUG ===');
    console.log('User context:', user);
    console.log('localStorage user:', localStorage.getItem('user'));
    console.log('localStorage token:', localStorage.getItem('token'));

    // Check if user context is valid, if not try to refresh from localStorage
    let currentUser = user;
    if (!currentUser || !currentUser.username) {
      console.log('User context invalid, trying to refresh from localStorage');
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        try {
          currentUser = JSON.parse(storedUser);
          console.log('Refreshed user from localStorage:', currentUser);
        } catch (err) {
          console.error('Error parsing stored user:', err);
        }
      }
    }

    if (!selectedFile || !currentUser?.username) {
      console.error('Missing required data:', { selectedFile: !!selectedFile, username: currentUser?.username, user: currentUser });
      alert('Missing required data for post creation. Please try logging in again.');
      return;
    }

    console.log('Creating post with:', { caption, location, username: currentUser.username, userId: currentUser._id });

    try {
      let userId = currentUser._id;

      // If user._id is not available, look it up by username
      if (!userId) {
        console.log('User ID not found in context, looking up by username:', currentUser.username);
        const userRes = await axios.get(`/api/users/${currentUser.username}`);
        console.log('User lookup response:', userRes.data);

        if (!userRes.data || !userRes.data._id) {
          console.error('Invalid user response:', userRes.data);
          throw new Error('User data not found or invalid');
        }

        userId = userRes.data._id;
      } else {
        console.log('Using user ID from context:', userId);
      }

      const form = new FormData();
      form.append('photo', selectedFile);
      form.append('caption', caption);
      form.append('location', location);
      form.append('user', userId);
      form.append('postType', postType);

      console.log('Form data being sent:', {
        caption: form.get('caption'),
        location: form.get('location'),
        user: form.get('user')
      });

      await axios.post('/api/posts', form);
      navigate('/profile/' + currentUser.username);
    } catch (err) {
      console.error('Create post error:', err);
      console.error('Error details:', err.response?.data);
      console.error('Error stack:', err.stack);
      alert('Failed to create post: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleBack = () => {
    if (step === 'edit') {
      setStep('select');
      setSelectedFile(null);
      setPreviewUrl(null);
      setPostType('photo');
      setIsVideoPlaying(false);
      setIsMuted(false);
    } else if (step === 'share') {
      setStep('edit');
    } else {
      navigate('/');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Cleanup video URL on unmount
  useEffect(() => {
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
      }
    };
  }, [previewUrl]);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      {/* Header */}
      <div className="sticky top-0 bg-white dark:bg-gray-800 border-b border-gray-300 dark:border-gray-700 z-10">
        <div className="flex items-center justify-between p-4">
          <Button variant="ghost" onClick={handleBack}>
            <ArrowLeft className="h-6 w-6" />
          </Button>
          <h1 className="font-semibold dark:text-white">Create new post</h1>
          {step === 'edit' && (
            <Button
              variant="ghost"
              className="text-blue-500 font-semibold"
              onClick={() => setStep('share')}
            >
              Next
            </Button>
          )}
          {step === 'share' && (
            <Button
              variant="ghost"
              className="text-blue-500 font-semibold"
              onClick={handleShare}
            >
              Share
            </Button>
          )}
          {step === 'select' && <div className="w-16"></div>}
        </div>
      </div>

      {step === 'select' && (
        <div className="flex flex-col items-center justify-center h-full py-20">
          <div className="text-center mb-8">
            <div className="w-24 h-24 mx-auto mb-4 rounded-full bg-gray-100 dark:bg-gray-700 flex items-center justify-center">
              {postType === 'reel' ? (
                <Video className="h-12 w-12 text-gray-400 dark:text-gray-500" />
              ) : (
                <Image className="h-12 w-12 text-gray-400 dark:text-gray-500" />
              )}
            </div>
            <h2 className="text-xl font-light mb-2 dark:text-white">
              {postType === 'reel' ? 'Share a reel' : 'Share a photo'}
            </h2>
            <p className="text-gray-500 dark:text-gray-400">
              {postType === 'reel' 
                ? 'When you share videos, they\'ll appear on your profile as reels.' 
                : 'When you share photos, they\'ll appear on your profile.'
              }
            </p>
          </div>

          <Button
            onClick={() => fileInputRef.current?.click()}
            className="bg-blue-500 hover:bg-blue-600"
          >
            {postType === 'reel' ? 'Select video from computer' : 'Select photo from computer'}
          </Button>

          <input
            ref={fileInputRef}
            type="file"
            accept={postType === 'reel' ? 'video/*' : 'image/*'}
            onChange={handleFileSelect}
            className="hidden"
          />

          <div className="mt-8 flex space-x-4">
            <Button 
              variant={postType === 'photo' ? 'default' : 'outline'} 
              size="sm"
              onClick={() => handlePostTypeSelect('photo')}
            >
              <Image className="h-4 w-4 mr-2" />
              Photo
            </Button>
            <Button 
              variant={postType === 'reel' ? 'default' : 'outline'} 
              size="sm"
              onClick={() => handlePostTypeSelect('reel')}
            >
              <Video className="h-4 w-4 mr-2" />
              Reel
            </Button>
          </div>
        </div>
      )}

      {step === 'edit' && previewUrl && (
        <div className="max-w-lg mx-auto">
          <div className={`${postType === 'reel' ? 'aspect-[9/16]' : 'aspect-square'} bg-gray-100 dark:bg-gray-800 mb-4 relative group`}>
            {postType === 'reel' ? (
              <div className="relative w-full h-full">
                <video
                  ref={videoRef}
                  src={previewUrl}
                  className="w-full h-full object-cover"
                  onLoadedMetadata={handleVideoLoad}
                  onTimeUpdate={handleVideoTimeUpdate}
                  onEnded={handleVideoEnd}
                  muted={isMuted}
                  loop
                  playsInline
                />

                {/* Video Controls Overlay */}
                <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all duration-300 flex items-center justify-center">
                  <Button
                    variant="ghost"
                    size="lg"
                    className="opacity-0 group-hover:opacity-100 transition-opacity bg-black bg-opacity-50 hover:bg-opacity-70 text-white"
                    onClick={toggleVideoPlay}
                  >
                    {isVideoPlaying ? <Pause className="h-8 w-8" /> : <Play className="h-8 w-8" />}
                  </Button>
                </div>

                {/* Video Progress Bar */}
                <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black to-transparent p-4">
                  <div className="flex items-center justify-between text-white text-sm mb-2">
                    <span>{formatTime(videoCurrentTime)}</span>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="text-white hover:bg-white hover:bg-opacity-20"
                      onClick={toggleMute}
                    >
                      {isMuted ? <VolumeX className="h-4 w-4" /> : <Volume2 className="h-4 w-4" />}
                    </Button>
                    <span>{formatTime(videoDuration)}</span>
                  </div>
                  <div className="w-full bg-white bg-opacity-30 rounded-full h-1">
                    <div 
                      className="bg-white h-1 rounded-full transition-all duration-100"
                      style={{ width: `${(videoCurrentTime / videoDuration) * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            ) : (
              <img
                src={previewUrl}
                alt="Preview"
                className="w-full h-full object-cover"
              />
            )}
          </div>

          <div className="p-4">
            <h3 className="font-semibold mb-4 dark:text-white">Edit</h3>
            <div className="grid grid-cols-4 gap-2 mb-4">
              {/* Filter options */}
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 dark:bg-gray-600 rounded-lg mb-1"></div>
                <span className="text-xs dark:text-white">Original</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 dark:bg-gray-600 rounded-lg mb-1"></div>
                <span className="text-xs dark:text-white">Clarendon</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 dark:bg-gray-600 rounded-lg mb-1"></div>
                <span className="text-xs dark:text-white">Gingham</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 dark:bg-gray-600 rounded-lg mb-1"></div>
                <span className="text-xs dark:text-white">Moon</span>
              </div>
            </div>

            <div className="flex justify-between mt-6">
              <Button 
                variant="outline" 
                onClick={() => setStep('select')}
              >
                Back
              </Button>
              <Button 
                onClick={() => setStep('share')}
                className="bg-blue-500 hover:bg-blue-600"
              >
                Next
              </Button>
            </div>
          </div>
        </div>
      )}

      {step === 'share' && previewUrl && (
        <div className="max-w-lg mx-auto p-4">
          <div className="flex space-x-4 mb-6">
            <div className={`${postType === 'reel' ? 'w-16 h-28' : 'w-16 h-16'} rounded-lg overflow-hidden flex-shrink-0`}>
              {postType === 'reel' ? (
                <video
                  src={previewUrl}
                  className="w-full h-full object-cover"
                  muted={false}
                  loop
                  autoPlay
                  playsInline
                />
              ) : (
                <img
                  src={previewUrl}
                  alt="Preview"
                  className="w-full h-full object-cover"
                />
              )}
            </div>
            <div className="flex-1">
              <Textarea
                placeholder="Write a caption..."
                value={caption}
                onChange={(e) => setCaption(e.target.value)}
                className="border-none resize-none focus:ring-0 p-0 text-base dark:text-white dark:bg-transparent"
                rows={3}
              />
              <div className="flex items-center justify-between mt-2">
                <Button variant="ghost" size="sm" className="p-0 text-gray-500">
                  <Smile className="h-5 w-5" />
                </Button>
                <span className="text-xs text-gray-500 dark:text-gray-400">{caption.length}/2,200</span>
              </div>
            </div>
          </div>

          <div className="space-y-4">
            <div className="flex items-center justify-between py-3 border-b border-gray-200 dark:border-gray-700">
              <span className="dark:text-white">Add location</span>
              <Input
                type="text"
                placeholder="Where was this taken?"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="border-none text-right focus:ring-0 w-48 dark:text-white dark:bg-transparent"
              />
            </div>

            <div className="flex items-center justify-between py-3 border-b border-gray-200 dark:border-gray-700">
              <span className="dark:text-white">Accessibility</span>
              <Button variant="ghost" size="sm" className="text-gray-500 dark:text-gray-400">
                Write alt text
              </Button>
            </div>

            <div className="flex items-center justify-between py-3 border-b border-gray-200 dark:border-gray-700">
              <span className="dark:text-white">Advanced settings</span>
              <Button variant="ghost" size="sm" className="text-gray-500 dark:text-gray-400">
                Settings
              </Button>
            </div>
          </div>

          <div className="flex justify-between mt-6">
            <Button 
              variant="outline" 
              onClick={() => setStep('edit')}
            >
              Back
            </Button>
            <Button 
              onClick={handleShare}
              className="bg-blue-500 hover:bg-blue-600"
            >
              Share
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
