import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Send, Smile, Image, Heart } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { useAuth } from '../components/AuthGuard';

const Messages = () => {
  const { user } = useAuth();
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Fetch all users except current user for chat list
    axios.get('/api/users').then(res => {
      setChats(res.data.filter(u => u._id !== user._id));
    });
  }, [user]);

  useEffect(() => {
    if (selectedChat) {
      axios.get(`/api/messages/${user._id}`)
        .then(res => setChatMessages(res.data.filter(m => m.sender === selectedChat._id || m.receiver === selectedChat._id)))
        .catch(() => setChatMessages([]));
    }
  }, [selectedChat, user]);

  const handleSendMessage = async () => {
    if (newMessage.trim() && selectedChat) {
      await axios.post('/api/messages', {
        sender: user._id,
        receiver: selectedChat._id,
        text: newMessage
      });
      setNewMessage('');
      // Refresh messages
      axios.get(`/api/messages/${user._id}`)
        .then(res => setChatMessages(res.data.filter(m => m.sender === selectedChat._id || m.receiver === selectedChat._id)))
        .catch(() => setChatMessages([]));
    }
  };

  return (
    <div className="h-screen bg-white flex">
      {/* Chat list */}
      <div className={`w-full lg:w-80 border-r border-gray-300 flex flex-col ${selectedChat ? 'hidden lg:flex' : ''}`}>
        {/* Header */}
        <div className="p-4 border-b border-gray-300">
          <div className="flex items-center justify-between">
            <h1 className="text-xl font-semibold">Messages</h1>
          </div>
        </div>

        {/* Chat list */}
        <div className="flex-1 overflow-y-auto">
          {chats.map((chat) => (
            <div
              key={chat.id}
              className={`flex items-center p-4 hover:bg-gray-50 cursor-pointer border-b border-gray-100 ${
                selectedChat?.id === chat.id ? 'bg-gray-50' : ''
              }`}
              onClick={() => setSelectedChat(chat)}
            >
              <div className="relative mr-3">
                <div className="w-12 h-12 rounded-full overflow-hidden">
                  <img
                    src={chat.avatar}
                    alt={chat.username}
                    className="w-full h-full object-cover"
                  />
                </div>
                {chat.online && (
                  <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                )}
              </div>
              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between">
                  <h3 className="font-semibold text-sm truncate">{chat.username}</h3>
                  <span className="text-xs text-gray-500">{chat.timestamp}</span>
                </div>
                <p className={`text-sm truncate ${chat.unread ? 'font-semibold text-black' : 'text-gray-500'}`}>
                  {chat.lastMessage}
                </p>
              </div>
              {chat.unread && (
                <div className="w-2 h-2 bg-blue-500 rounded-full ml-2"></div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Chat window */}
      <div className={`flex-1 flex flex-col ${!selectedChat ? 'hidden lg:flex' : ''}`}>
        {selectedChat ? (
          <>
            {/* Chat header */}
            <div className="p-4 border-b border-gray-300 flex items-center">
              <div className="relative mr-3">
                <div className="w-8 h-8 rounded-full overflow-hidden">
                  <img
                    src={selectedChat.avatar}
                    alt={selectedChat.username}
                    className="w-full h-full object-cover"
                  />
                </div>
                {selectedChat.online && (
                  <div className="absolute bottom-0 right-0 w-2 h-2 bg-green-500 rounded-full border border-white"></div>
                )}
              </div>
              <div>
                <h3 className="font-semibold">{selectedChat.username}</h3>
                {selectedChat.online && (
                  <p className="text-xs text-gray-500">Active now</p>
                )}
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {chatMessages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${message.isOwn ? 'justify-end' : 'justify-start'}`}
                >
                  <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl ${
                    message.isOwn 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-100 text-black'
                  }`}>
                    <p className="text-sm">{message.text}</p>
                  </div>
                </div>
              ))}
            </div>

            {/* Message input */}
            <div className="p-4 border-t border-gray-300">
              <div className="flex items-center space-x-2">
                <Button variant="ghost" size="sm" className="p-2">
                  <Image className="h-5 w-5" />
                </Button>
                <div className="flex-1">
                  <Input
                    type="text"
                    placeholder="Message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    className="border-none bg-gray-100 rounded-full px-4 focus:ring-1 focus:ring-gray-300"
                  />
                </div>
                <Button variant="ghost" size="sm" className="p-2">
                  <Smile className="h-5 w-5" />
                </Button>
                {newMessage.trim() ? (
                  <Button
                    onClick={handleSendMessage}
                    className="bg-blue-500 hover:bg-blue-600 rounded-full px-4"
                  >
                    Send
                  </Button>
                ) : (
                  <Button variant="ghost" size="sm" className="p-2">
                    <Heart className="h-5 w-5" />
                  </Button>
                )}
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <div className="w-24 h-24 mx-auto mb-4 rounded-full border-2 border-gray-300 flex items-center justify-center">
                <Send className="h-12 w-12 text-gray-300" />
              </div>
              <h3 className="text-xl font-light mb-2">Your Messages</h3>
              <p className="text-gray-500">Send private photos and messages to a friend or group.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
