import React, { useState, useEffect } from 'react';
import StoriesCarousel from '../components/StoriesCarousel';
import PostCard from '../components/PostCard';
import axios from 'axios';
import { useAuth } from '../components/AuthGuard';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await axios.get('/api/posts');
        setPosts(res.data);
      } catch (err) {
        console.error('Error fetching posts:', err);
        setPosts([]);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  if (loading) {
    return (
      <div className="max-w-xl mx-auto lg:max-w-2xl">
        <div className="bg-white border-b border-gray-300 lg:border lg:rounded-lg lg:mb-6">
          <StoriesCarousel />
        </div>
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto lg:max-w-2xl">
      {/* Stories */}
      <div className="bg-white border-b border-gray-300 lg:border lg:rounded-lg lg:mb-6">
        <StoriesCarousel />
      </div>

      {/* Posts Feed */}
      <div className="space-y-0 lg:space-y-6">
        {posts.length > 0 ? (
          posts.map((post) => (
            <PostCard key={post._id} post={post} />
          ))
        ) : (
          <div className="bg-white border-b border-gray-300 lg:border lg:rounded-lg p-8 text-center">
            <p className="text-gray-500">No posts yet. Create your first post!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
