import React, { useState, useRef } from 'react';
import axios from 'axios';
import { ArrowLeft, Image, Video, Smile } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Textarea } from '../components/ui/textarea';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthGuard';

const CreatePost = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  const { user } = useAuth();
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [caption, setCaption] = useState('');
  const [location, setLocation] = useState('');
  const [step, setStep] = useState('select'); // select, edit, share

  const handleFileSelect = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
      setStep('edit');
    }
  };

  const handleShare = async () => {
    if (!selectedFile || !user?.username) return;

    console.log('Creating post with:', { caption, location, username: user.username });

    // First, get the user's _id from the backend
    try {
      const userRes = await axios.get(`/api/users/${user.username}`);
      const userId = userRes.data._id;

      const form = new FormData();
      form.append('photo', selectedFile);
      form.append('caption', caption);
      form.append('location', location);
      form.append('user', userId);

      console.log('Form data being sent:', {
        caption: form.get('caption'),
        location: form.get('location'),
        user: form.get('user')
      });

      await axios.post('/api/posts', form);
      navigate('/profile/' + user.username);
    } catch (err) {
      console.error('Create post error:', err);
      alert('Failed to create post: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleBack = () => {
    if (step === 'edit') {
      setStep('select');
      setSelectedFile(null);
      setPreviewUrl(null);
    } else if (step === 'share') {
      setStep('edit');
    } else {
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="sticky top-0 bg-white border-b border-gray-300 z-10">
        <div className="flex items-center justify-between p-4">
          <Button variant="ghost" onClick={handleBack}>
            <ArrowLeft className="h-6 w-6" />
          </Button>
          <h1 className="font-semibold">Create new post</h1>
          {step === 'edit' && (
            <Button
              variant="ghost"
              className="text-blue-500 font-semibold"
              onClick={() => setStep('share')}
            >
              Next
            </Button>
          )}
          {step === 'share' && (
            <Button
              variant="ghost"
              className="text-blue-500 font-semibold"
              onClick={handleShare}
            >
              Share
            </Button>
          )}
          {step === 'select' && <div className="w-16"></div>}
        </div>
      </div>

      {step === 'select' && (
        <div className="flex flex-col items-center justify-center h-full py-20">
          <div className="text-center mb-8">
            <div className="w-24 h-24 mx-auto mb-4 rounded-full bg-gray-100 flex items-center justify-center">
              <Image className="h-12 w-12 text-gray-400" />
            </div>
            <h2 className="text-xl font-light mb-2">Share photos and videos</h2>
            <p className="text-gray-500">When you share photos and videos, they'll appear on your profile.</p>
          </div>

          <Button
            onClick={() => fileInputRef.current?.click()}
            className="bg-blue-500 hover:bg-blue-600"
          >
            Select from computer
          </Button>

          <input
            ref={fileInputRef}
            type="file"
            accept="image/*,video/*"
            onChange={handleFileSelect}
            className="hidden"
          />

          <div className="mt-8 flex space-x-4">
            <Button variant="outline" size="sm">
              <Image className="h-4 w-4 mr-2" />
              Photo
            </Button>
            <Button variant="outline" size="sm">
              <Video className="h-4 w-4 mr-2" />
              Video
            </Button>
          </div>
        </div>
      )}

      {step === 'edit' && previewUrl && (
        <div className="max-w-lg mx-auto">
          <div className="aspect-square bg-gray-100 mb-4">
            <img
              src={previewUrl}
              alt="Preview"
              className="w-full h-full object-cover"
            />
          </div>

          <div className="p-4">
            <h3 className="font-semibold mb-4">Edit</h3>
            <div className="grid grid-cols-4 gap-2 mb-4">
              {/* Filter options */}
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 rounded-lg mb-1"></div>
                <span className="text-xs">Original</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 rounded-lg mb-1"></div>
                <span className="text-xs">Clarendon</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 rounded-lg mb-1"></div>
                <span className="text-xs">Gingham</span>
              </div>
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 rounded-lg mb-1"></div>
                <span className="text-xs">Moon</span>
              </div>
            </div>

            <div className="flex justify-between mt-6">
              <Button 
                variant="outline" 
                onClick={() => setStep('select')}
              >
                Back
              </Button>
              <Button 
                onClick={() => setStep('share')}
                className="bg-blue-500 hover:bg-blue-600"
              >
                Next
              </Button>
            </div>
          </div>
        </div>
      )}

      {step === 'share' && previewUrl && (
        <div className="max-w-lg mx-auto p-4">
          <div className="flex space-x-4 mb-6">
            <div className="w-16 h-16 rounded-lg overflow-hidden flex-shrink-0">
              <img
                src={previewUrl}
                alt="Preview"
                className="w-full h-full object-cover"
              />
            </div>
            <div className="flex-1">
              <Textarea
                placeholder="Write a caption..."
                value={caption}
                onChange={(e) => setCaption(e.target.value)}
                className="border-none resize-none focus:ring-0 p-0 text-base"
                rows={3}
              />
              <div className="flex items-center justify-between mt-2">
                <Button variant="ghost" size="sm" className="p-0 text-gray-500">
                  <Smile className="h-5 w-5" />
                </Button>
                <span className="text-xs text-gray-500">{caption.length}/2,200</span>
              </div>
            </div>
          </div>

          <div className="space-y-4">
            <div className="flex items-center justify-between py-3 border-b border-gray-200">
              <span>Add location</span>
              <Input
                type="text"
                placeholder="Where was this taken?"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="border-none text-right focus:ring-0 w-48"
              />
            </div>

            <div className="flex items-center justify-between py-3 border-b border-gray-200">
              <span>Accessibility</span>
              <Button variant="ghost" size="sm" className="text-gray-500">
                Write alt text
              </Button>
            </div>

            <div className="flex items-center justify-between py-3 border-b border-gray-200">
              <span>Advanced settings</span>
              <Button variant="ghost" size="sm" className="text-gray-500">
                Settings
              </Button>
            </div>
          </div>

          <div className="flex justify-between mt-6">
            <Button 
              variant="outline" 
              onClick={() => setStep('edit')}
            >
              Back
            </Button>
            <Button 
              onClick={handleShare}
              className="bg-blue-500 hover:bg-blue-600"
            >
              Share
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
